import React, { useState } from 'react';

const Count = (props) => {
  const style = {
    width: '100px',
    heigh: '50px',
    backgroundColor: 'gold',
    border: 'none',
    color: 'black',
    padding: '20px',
  };

  const [number, setNumber] = useState(0);
  const increase = () => {
    setNumber(number + 1);
  };

  const decrease = () => {
    setNumber(number - 1);
  };

  const updateProps = () => {
    props.setNick('김길동');
  };
  return (
    <>
      <div>
        <button onClick={increase} style={style}>
          증가
        </button>
        <button onClick={decrease} style={style}>
          감소
        </button>
      </div>
      <div>
        <h1>{number}</h1>
      </div>
      <button onClick={updateProps}>prop변경</button>
      <div>
        <h1>{props.nick}</h1>
      </div>
    </>
  );
};
export default Count;
