import React, { useState } from 'react';

const Count = (props) => {
  const [number, setNumber] = useState(0);
  const increase = () => {
    setNumber(number + 1);
  };

  const decrease = () => {
    setNumber(number - 1);
  };

  const updateProps = () => {
    props.setName('김길동');
  };
  return (
    <>
      <div>
        <button onClick={increase}>증가</button>
        <button onClick={decrease}>감소</button>
      </div>
      <div>{number}</div>
      <button onClick={updateProps}>prop변경</button>
      <div>{props.name}</div>
    </>
  );
};
export default Count;
