import Loop3 from './Loop3';
import Count from './Count';
import React, { useState } from 'react';

function App() {
  const name = 'React';
  const greet = true;
  const style = {
    backgroundColor: 'red',
    color: 'gold',
  };
  let number = ['1', '2', '3', '4'];
  const [nick, setName] = useState('');

  return (
    <>
      {number.map((n, i) => (
        <li key={i}>
          <Greet />
        </li>
      ))}
      <Count setNumber={setName} nick={nick} />
      <Loop1 />
      <Loop2 />
      <Loop3 />
      <Greet />
      <h1>Hellow {name}</h1>
      {greet !== true ? <h2>안녕하세요</h2> : <h2>안녕하세요</h2>}
      {greet === true && <h3>안녕</h3>}
      <h2 style={style}>{name} 안녕하세용ㅇㅇㅇ</h2>
      <h2 style={{ backgroundColor: 'gold', color: 'green' }}>
        안녕하세요{name}
      </h2>
    </> //if문은 사용 불가 (조건문x 표현식o)
  );
