import {useState} from "react";
import axios from "axios";

const LoginForm = () =>{
    const [data, setData] = useState({id:'', pwd:''}); //두가지를 객체화 하여 한번에 저장
    const handleChange = (e)=> {   //객체 두개를 처리하기 위한 과정
        setData({
            ...data,
            [e.target.name] : e.target.value,
        })
    }
    const handleSubmit = (e)=>{
        e.preventDefault();
        axios.get('http://localhost:8090/test/api/customer/login');
    };


    return(
        <>
            <form onSubmit={handleSubmit}>
                <input type="text" name="id" value={data.id} onClick={handleChange}/><br />
                <input type="password" name="pwd" value={data.pwd} onClick={handleChange}/>
                <br/>
                <input type="submit" value="로그인"/>

            </form>
        </>
    );
};
export default LoginForm;
